static PyObject *
VectorAttributes_SetVectorColor(PyObject *self, PyObject *args)
{
    VectorAttributesObject *obj = (VectorAttributesObject *)self;

    int r,g,b,a;
    if(!PyArg_ParseTuple(args, "iiii", &r, &g, &b, &a))
    {
        a = 255;
        if(!PyArg_ParseTuple(args, "iii", &r, &g, &b))
        {
            PyObject *tuple = NULL;
            if(!PyArg_ParseTuple(args, "O", &tuple))
                return NULL;

            if(!PyTuple_Check(tuple))
                return NULL;

            // Make sure that the tuple is the right size.
            if(PyTuple_Size(tuple) < 3 || PyTuple_Size(tuple) > 4)
                return NULL;

            // Make sure that all elements in the tuple are ints.
            for(int i = 0; i < PyTuple_Size(tuple); ++i)
            {
                PyObject *item = PyTuple_GET_ITEM(tuple, i);
                    if(!PyInt_Check(item))
                return NULL;
            }

            // Set the r,g,b,a values from the tuple.
            r = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 0)));
            g = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 1)));
            b = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 2)));
            if(PyTuple_Size(tuple) > 3)
                a = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 3)));
        }
        PyErr_Clear();
    }

    // Set the vectorColor in the object.
    ColorAttribute ca(r, g, b, a);
    obj->data->SetVectorColor(ca);

    Py_INCREF(Py_None);
    return Py_None;
}

