"AddOperator(\"Clip\")\n"
"c = ClipAttributes()\n"
"c.plane1Origin = (0,0.6,0)\n"
"c.plane1Normal = (0,-1,0)\n"
"SetOperatorOption(c)\n"
"DrawPlots()\n"
"CreateNamedSelection(\"els_above_at_time_0\")\n"
"SetTimeSliderState(40)\n"
"RemoveLastOperator()\n"
"ApplyNamedSelection(\"els_above_at_time_0\")\n"
;
