"e = ExportDBAttributes()\n"
"e.db_type = \"Silo\"\n"
"e.variables = (\"u\", \"v\")\n"
"e.filename = \"test_ex_db\"\n"
"# Set the export directory\n"
"e.dirname = \".\"\n"
"ExportDatabase(e)\n"
;
