"p = PseudocolorAttributes()\n"
"p.colorTableName = \"calewhite\"\n"
"p.minFlag,p.maxFlag = 1,1\n"
"p.min,p.max = -5.0, 8.0\n"
"SetPlotOptions(p)\n"
"DrawPlots()\n"
;
