    if(atts->GetEnabled())
        snprintf(tmpStr, 1000, "%senabled = 1\n", prefix);
    else
        snprintf(tmpStr, 1000, "%senabled = 0\n", prefix);
    str += tmpStr;
    snprintf(tmpStr, 1000, "%snFrames = %d\n", prefix, atts->GetNFrames());
