//
// Helper function for comparing.
//
static PyObject *
PointGlyphAttributes_richcompare(PyObject *self, PyObject *other, int op)
{
    // only compare against the same type 
    if ( Py_TYPE(self) != &PointGlyphAttributesType
         || Py_TYPE(other) != &PointGlyphAttributesType)
    {
        Py_INCREF(Py_NotImplemented);
        return Py_NotImplemented;
    }

    PyObject *res = NULL;
    PointGlyphAttributes *a = ((PointGlyphAttributesObject *)self)->data;
    PointGlyphAttributes *b = ((PointGlyphAttributesObject *)other)->data;

    switch (op)
    {
       case Py_EQ:
           res = (*a == *b) ? Py_True : Py_False;
           break;
       case Py_NE:
           res = (*a != *b) ? Py_True : Py_False;
           break;
       default:
           res = Py_NotImplemented;
           break;
    }

    Py_INCREF(res);
    return res;
}

