    str += tmpStr;
    snprintf(tmpStr, 1000, "%sxLabel = \"%s\"\n", prefix, atts->xLabel.c_str());
    str += tmpStr;
    snprintf(tmpStr, 1000, "%syUnits = \"%s\"\n", prefix, atts->yUnits.c_str());
    str += tmpStr;
    snprintf(tmpStr, 1000, "%syLabel = \"%s\"\n", prefix, atts->yLabel.c_str());
